    }
  })

  if (!compiler.isConfigValid) {
    print('Compiler configuration is invalid', 4)
    print(compiler.configValidationMessages, 8)
  }

  const devServer = await compiler.createDevServer({
    historyApiFallback: true,
    noInfo: false,
